DO $$
DECLARE
    max_id INT;
BEGIN
    -- Step 1: Get the maximum id from the 'order' table
    SELECT MAX(id) INTO max_id FROM "checkout_cards";
    
    -- Step 2: Alter the sequence to restart with max_id + 1
    EXECUTE 'ALTER SEQUENCE checkout_cards_id_seq RESTART WITH ' || (max_id + 1000);
END $$;
Select 'Altering Sequence ID' as Updated;

