DO $$
DECLARE
    max_id INT;
BEGIN
    -- Step 1: Get the maximum id from the 'order' table
    SELECT MAX(id) INTO max_id FROM "payment_option";

    -- Step 2: Alter the sequence to restart with max_id + 1
    EXECUTE 'ALTER SEQUENCE payment_option_id_seq RESTART WITH ' || (max_id + 1000);
END $$;
Select 'Altering Sequence ID' as Update;

WITH cte AS (
    SELECT st.product_id, st.taxon_id AS temp_taxon_id
    FROM product_taxon st
)
UPDATE product p
SET temp_taxon_id = cte.temp_taxon_id
FROM cte
WHERE p.id = cte.product_id;
SELECT 'Updating Temp Taxon ID' AS Message;

alter table product drop column temp_taxon_id;
alter table product drop column temp_supplier_code;
alter table brand drop column temp_taxon_id;
SELECT 'Remove unwanted Columns' as Removed;

SELECT DISTINCT product_id,
       taxon_id
FROM   spree_products_taxons;
