DO $$
DECLARE
    max_id INT;
BEGIN
    -- Step 1: Get the maximum id from the 'order' table
    SELECT MAX(id) INTO max_id FROM "time_slot";
    
    -- Step 2: Alter the sequence to restart with max_id + 1
    EXECUTE 'ALTER SEQUENCE time_slot_id_seq RESTART WITH ' || (max_id + 1000);
END $$;
SELECT 'Altering Sequence' as 'Update';

