DO $$
DECLARE
    max_id INT;
BEGIN
    -- Step 1: Get the maximum id from the 'order' table
    SELECT MAX(id) INTO max_id FROM "order_address";
    
    -- Step 2: Alter the sequence to restart with max_id + 1
    EXECUTE 'ALTER SEQUENCE order_address_id_seq RESTART WITH ' || (max_id + 1);
END $$;
select max(updated_at) as max_updated_date from order_address;
