WITH cte AS (
    SELECT st.product_id, st.taxon_id AS temp_taxon_id
    FROM product_taxon st
)
UPDATE product p
SET temp_taxon_id = cte.temp_taxon_id
FROM cte
WHERE p.id = cte.product_id;
SELECT 'Updating Temp Taxon ID' AS Message;

With cte AS
  (select id, name as supplier_code 
  from supplier
)
UPDATE product p
SET supplier_id = cte.id
FROM cte
WHERE p.temp_supplier_code = cte.supplier_code;
SELECT 'Update Supplier_Code' as UpdateCode;
